import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { endpoints } from '../config/api';

function NewAppointmentModal({ isOpen, onClose }) {
  const [doctors, setDoctors] = useState([]);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [formData, setFormData] = useState({
    doctorId: '',
    date: '',
    time: '',
    reason: ''
  });
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      loadDoctors();
    }
  }, [isOpen]);

  const loadDoctors = async () => {
    try {
      const response = await axios.get(endpoints.doctors.list);
      setDoctors(response.data);
    } catch (err) {
      console.error('Error al cargar doctores:', err);
      setError('Error al cargar los doctores');
    }
  };

  const loadAvailableSlots = async (doctorId, date) => {
    if (!doctorId || !date) return;

    try {
      // Formatear la fecha para asegurar el formato YYYY-MM-DD
      const formattedDate = new Date(date).toISOString().split('T')[0];
      const response = await axios.get(endpoints.appointments.available(doctorId, formattedDate));
      setAvailableSlots(response.data);
    } catch (err) {
      console.error('Error al cargar horarios:', err);
      setError('Error al cargar los horarios disponibles');
      setAvailableSlots([]);
    }
  };

  const handleDoctorChange = (e) => {
    const newDoctorId = e.target.value;
    setFormData({
      ...formData,
      doctorId: newDoctorId,
      time: '' // Resetear la hora cuando cambia el doctor
    });
    if (formData.date && newDoctorId) {
      loadAvailableSlots(newDoctorId, formData.date);
    }
  };

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setFormData({
      ...formData,
      date: selectedDate,
      time: '' // Resetear la hora cuando cambia la fecha
    });
    if (formData.doctorId && selectedDate) {
      loadAvailableSlots(formData.doctorId, selectedDate);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Crear un objeto de fecha combinando la fecha y hora
      const appointmentDateTime = new Date(formData.date);
      const [hours, minutes] = formData.time.split(':');
      appointmentDateTime.setHours(parseInt(hours), parseInt(minutes), 0, 0);

      const appointmentData = {
        doctorId: formData.doctorId,
        date: appointmentDateTime.toISOString(),
        reason: formData.reason
      };

      await axios.post(endpoints.appointments.create, appointmentData);
      onClose();
    } catch (err) {
      console.error('Error al crear cita:', err);
      setError('Error al programar la cita. Por favor, verifica los datos.');
    }
  };

  if (!isOpen) return null;

  // Obtener la fecha mÃ­nima (hoy) en formato YYYY-MM-DD
  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Programar Nueva Cita</h2>
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div>
            <label>Doctor:</label>
            <select 
              value={formData.doctorId} 
              onChange={handleDoctorChange}
              required
            >
              <option value="">Seleccione un doctor</option>
              {doctors.map(doctor => (
                <option key={doctor._id} value={doctor._id}>
                  Dr. {doctor.name} - {doctor.specialty}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Fecha:</label>
            <input
              type="date"
              value={formData.date}
              onChange={handleDateChange}
              min={today}
              required
            />
          </div>

          <div>
            <label>Hora:</label>
            <select
              value={formData.time}
              onChange={(e) => setFormData({ ...formData, time: e.target.value })}
              required
              disabled={!availableSlots.length}
            >
              <option value="">Seleccione un horario</option>
              {availableSlots.map(slot => (
                <option key={slot} value={slot}>
                  {slot}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Motivo de la consulta:</label>
            <textarea
              value={formData.reason}
              onChange={(e) => setFormData({ ...formData, reason: e.target.value })}
              required
              rows="3"
            />
          </div>

          <div className="modal-buttons">
            <button type="submit">Programar Cita</button>
            <button type="button" onClick={onClose} className="cancel-button">
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default NewAppointmentModal;
