import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import NewAppointmentModal from '../components/NewAppointmentModal';

function Appointments() {
  const [appointments, setAppointments] = useState([]);
  const [error, setError] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  const loadAppointments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/appointments');
      setAppointments(response.data);
    } catch (err) {
      setError('Error al cargar las citas');
    }
  };

  useEffect(() => {
    loadAppointments();
  }, []);

  const handleCancelAppointment = async (id) => {
    if (!window.confirm('¿Está seguro de cancelar esta cita?')) return;

    try {
      await axios.patch(`http://localhost:5000/api/appointments/${id}/status`, {
        status: 'cancelled',
      });
      loadAppointments();
    } catch (err) {
      setError('Error al cancelar la cita');
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'scheduled':
        return 'Programada';
      case 'completed':
        return 'Completada';
      case 'cancelled':
        return 'Cancelada';
      default:
        return status;
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="appointments-container">
      <h2>Mis Citas</h2>
      <div className="appointments-list">
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {appointments.length === 0 ? (
          <p>No hay citas programadas</p>
        ) : (
          <table className="appointments-table">
            <thead>
              <tr>
                <th>Fecha</th>
                <th>Hora</th>
                <th>Doctor</th>
                <th>Especialidad</th>
                <th>Motivo</th>
                <th>Estado</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((appointment) => (
                <tr key={appointment.id}>
                  <td>{formatDate(appointment.date)}</td>
                  <td>{appointment.time}</td>
                  <td>{appointment.doctor.name}</td>
                  <td>{appointment.doctor.specialty}</td>
                  <td>{appointment.reason}</td>
                  <td>
                    <span className={`status-badge ${appointment.status}`}>
                      {getStatusText(appointment.status)}
                    </span>
                  </td>
                  <td>
                    {appointment.status === 'scheduled' && (
                      <button 
                        className="cancel-button"
                        onClick={() => handleCancelAppointment(appointment.id)}
                      >
                        Cancelar
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      <div className="nav-buttons">
        <button onClick={() => setIsModalOpen(true)}>
          Nueva Cita
        </button>
        <button onClick={() => navigate('/login')}>
          Cerrar Sesión
        </button>
      </div>

      <NewAppointmentModal 
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          loadAppointments(); // Recargar las citas después de cerrar el modal
        }}
      />
    </div>
  );
}

export default Appointments;
